from material a where 1=1 and a.modifyTime IN(SELECT MAX(modifyTime) FROM material WHERE 1=1
and applicantId = #{material.applicantId,jdbcType=INTEGER}
AND sortId>0 GROUP BY sortId) GROUP BY fileName;

  	select 

    from material
    where 
    1=1	


   		and applicantId = #{material.applicantId,jdbcType=INTEGER} 		   		



