mapper.select:
    select 

    from trademark_case_pre
    where 1=1



				and (
				tmName like #{gcon.keyword,jdbcType=VARCHAR} or
				regNumber like #{gcon.keyword,jdbcType=VARCHAR} or
				appCnName like #{tmcase.appCnName,jdbcType=VARCHAR} or 
				appEnName like #{tmcase.appEnName,jdbcType=VARCHAR})




					and tmName like #{tmcase.tmName,jdbcType=VARCHAR}



					and regNumber like #{tmcase.regNumber,jdbcType=VARCHAR}



					and  appCnName like #{tmcase.appCnName,jdbcType=VARCHAR}



					and  appEnName like #{tmcase.appEnName,jdbcType=VARCHAR}





		and custId= #{tmcase.custId,jdbcType=INTEGER} 



		and agencyId = #{tmcase.agencyId,jdbcType=INTEGER} 



		and  appDate > #{tmcase.appStartTime,jdbcType=TIMESTAMP}




		and  appDate < #{tmcase.appEndTime,jdbcType=TIMESTAMP}




		and  regDate > #{tmcase.regStartTime,jdbcType=TIMESTAMP}




		and  regDate < #{tmcase.regEndTime,jdbcType=TIMESTAMP}




		order by ${gcon.orderCol,jdbcType=VARCHAR} ${gcon.orderAsc,jdbcType=VARCHAR}				




	 limit #{gcon.offset,jdbcType=INTEGER}, #{gcon.rows,jdbcType=INTEGER}

    select count(*) as count
    from trademark_case_pre
    where 1=1



				and (
				tmName like #{gcon.keyword,jdbcType=VARCHAR} or
				regNumber like #{gcon.keyword,jdbcType=VARCHAR} or
				appCnName like #{tmcase.appCnName,jdbcType=VARCHAR} or 
				appEnName like #{tmcase.appEnName,jdbcType=VARCHAR})




					and tmName like #{tmcase.tmName,jdbcType=VARCHAR}



					and regNumber like #{tmcase.regNumber,jdbcType=VARCHAR}



					and  appCnName like #{tmcase.appCnName,jdbcType=VARCHAR}



					and  appEnName like #{tmcase.appEnName,jdbcType=VARCHAR}





		and custId= #{tmcase.custId,jdbcType=INTEGER} 



		and agencyId = #{tmcase.agencyId,jdbcType=INTEGER} 



		and  appDate > #{tmcase.appStartTime,jdbcType=TIMESTAMP}




		and  appDate < #{tmcase.appEndTime,jdbcType=TIMESTAMP}




		and  regDate > #{tmcase.regStartTime,jdbcType=TIMESTAMP}




		and  regDate < #{tmcase.regEndTime,jdbcType=TIMESTAMP}
